{
  "performance_comparison": {
    "type": "bar",
    "data": {
      "labels": ["Mirae Asset Large Cap", "Parag Parikh Flexi Cap", "PGIM India Midcap", "Quant Small Cap", "ICICI Pru Medium Term Bond"],
      "datasets": [
        {
          "label": "1Y CAGR (%)",
          "data": [15.0, 22.0, 35.0, 45.0, 7.0],
          "backgroundColor": "rgba(255, 99, 132, 0.5)"
        },
        {
          "label": "3Y CAGR (%)",
          "data": [19.0, 26.0, 32.0, 40.0, 6.8],
          "backgroundColor": "rgba(54, 162, 235, 0.5)"
        },
        {
          "label": "5Y CAGR (%)",
          "data": [18.0, 24.0, 29.0, 35.0, 7.2],
          "backgroundColor": "rgba(75, 192, 192, 0.5)"
        }
      ]
    },
    "options": {
      "indexAxis": "y",
      "responsive": true,
      "plugins": {
        "title": {
          "display": true,
          "text": "Fund Performance Comparison (CAGR)"
        }
      }
    }
  },
  "risk_return_scatter": {
    "type": "scatter",
    "data": {
      "datasets": [
        {
          "label": "Funds",
          "data": [
            {"x": 0.16, "y": 19.0, "fundName": "Mirae Asset Large Cap"},
            {"x": 0.14, "y": 26.0, "fundName": "Parag Parikh Flexi Cap"},
            {"x": 0.18, "y": 32.0, "fundName": "PGIM India Midcap"},
            {"x": 0.21, "y": 40.0, "fundName": "Quant Small Cap"},
            {"x": 0.03, "y": 6.8, "fundName": "ICICI Pru Medium Term Bond"}
          ],
          "backgroundColor": "rgba(75, 192, 192, 0.8)"
        }
      ]
    },
    "options": {
      "responsive": true,
      "plugins": {
        "title": {
          "display": true,
          "text": "Risk vs. Return (Std. Dev vs. 3Y CAGR)"
        },
        "tooltip": {
          "callbacks": {
            "label": function(context) {
              let label = context.dataset.data[context.dataIndex].fundName || '';
              if (label) { label += ': '; }
              label += `(Risk: ${context.parsed.x}, Return: ${context.parsed.y}%)`;
              return label;
            }
          }
        }
      },
      "scales": {
        "x": {
          "title": {
            "display": true,
            "text": "Standard Deviation (Risk)"
          }
        },
        "y": {
          "title": {
            "display": true,
            "text": "3Y CAGR (%) (Return)"
          }
        }
      }
    }
  },
  "portfolio_allocation": {
    "asset_class": {
      "type": "doughnut",
      "data": {
        "labels": ["Equity", "Debt"],
        "datasets": [
          {
            "label": "Allocation",
            "data": [70, 30],
            "backgroundColor": ["rgba(54, 162, 235, 0.8)", "rgba(255, 206, 86, 0.8)"]
          }
        ]
      },
      "options": {
        "responsive": true,
        "plugins": {
          "title": {
            "display": true,
            "text": "Portfolio Asset Allocation"
          }
        }
      }
    },
    "sub_category": {
      "type": "bar",
      "data": {
        "labels": ["Large Cap", "Flexi Cap", "Mid Cap", "Small Cap"],
        "datasets": [
          {
            "label": "Equity Allocation %",
            "data": [20, 20, 15, 15],
            "backgroundColor": ["rgba(75, 192, 192, 0.5)", "rgba(153, 102, 255, 0.5)", "rgba(255, 159, 64, 0.5)", "rgba(255, 99, 132, 0.5)"]
          }
        ]
      },
      "options": {
        "responsive": true,
        "plugins": {
          "title": {
            "display": true,
            "text": "Equity Sub-Category Breakdown"
          }
        }
      }
    }
  },
  "expense_ratio_comparison": {
    "type": "bar",
    "data": {
      "labels": ["Mirae Asset Large Cap", "Parag Parikh Flexi Cap", "PGIM India Midcap", "Quant Small Cap", "ICICI Pru Medium Term Bond"],
      "datasets": [
        {
          "label": "Expense Ratio (%)",
          "data": [0.51, 0.62, 0.44, 0.77, 0.98],
          "backgroundColor": "rgba(153, 102, 255, 0.5)"
        }
      ]
    },
    "options": {
      "responsive": true,
      "plugins": {
        "title": {
          "display": true,
          "text": "Expense Ratio Comparison"
        }
      }
    }
  }
}

